window.onload= () => {
    Particles.init({
        selector: '.background',
        connectParticles: true,
        color: ['800080'],
    });

};

// Responsive particles
Particles.init({
    
    // normal options
    selector: '.background',
    maxParticles: 100,
    
    // options for breakpoints
    responsive: [
        {
        breakpoint: 768,
        options: {
            maxParticles: 200,
            color: ['800080'],
            connectParticles: false
        }
        }, {
        breakpoint: 425,
        options: {
            maxParticles: 100,
            color: ['800080'],
            connectParticles: false
        }
        }, {
        breakpoint: 320,
        options: {
            maxParticles: 0 // disables particles.js
        }
    }
]
});